import AxiosRequest from "../utils/Axios";

function Create({handleCreate, handleDisplay}) {
    const handleSubmit = (e) => {
        e.preventDefault();
        const brand = document.getElementById("brand").value;
        const model = document.getElementById("model").value;
        const make = document.getElementById("make").value;
        const description = document.getElementById("description").value;
        const price = document.getElementById("price").value;

        if (!brand || !name || !description || !price) {
            document.getElementById("alert").classList.remove("d-none");
            document.querySelectorAll(".create").forEach((input) => {
                if (!input.value) {
                    input.classList.add("is-invalid");
                } else {
                    input.classList.remove("is-invalid");
                }
            });
            return;
        }

        AxiosRequest({
            url: "/products",
            method: "POST",
            data: {
                brand: brand,
                name: name,
                description: description,
                price: price,
            },
        })
        .then((res) => {
            handleCreate(res.data);
            handleDisplay();
        })
        .catch((err) => {
            console.log(err);
        });
    }

    return (
        <div className="position-fixed top-50 start-50 translate-middle w-100 h-100 z-3 bg-black bg-opacity-75">
            <div className="container bg-white text-black p-3 p-md-5 w-100 position-relative rounded rounded-3 border border-secondary mt-4" style={{maxWidth: "50%", maxHeight: "90vh", overflowY: "auto", overflowX: "hidden"}}>
                <button className="btn-close position-absolute top-0 end-0 m-3" onClick={() => handleDisplay()}></button>
                <h1>Create</h1>
                <p className="text-muted m-0">Fill in the form below to create a new product.</p>
                <hr className="mt-1"/>

                <p id="alert" className="alert alert-danger d-none p-1">Please fill in all fields.</p>
                <form onSubmit={handleSubmit} onChange={(e) => {
                    e.target.classList.remove("is-invalid")
                    document.getElementById("alert").classList.add("d-none");
                }}>
                    <div className="mb-3">
                        <label htmlFor="brand" className="form-label">Brand</label>
                        <input type="text" className="form-control create" id="brand" />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="model" className="form-label">Model</label>
                        <input type="text" className="form-control create" id="model" />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="make" className="form-label">Make</label>
                        <input type="text" className="form-control create" id="make" />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="description" className="form-label">Description</label>
                        <textarea className="form-control create" id="description" rows="3" maxLength="100" ></textarea>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="price" className="form-label">Price</label>
                        <input type="number" className="form-control create" id="price" step="0.01"/>
                    </div>
                    <button type="submit" className="btn btn-primary">Submit</button>
                </form>
            </div>
        </div>
    );
}

export default Create;
